import React from "react";
import data from "./data";
import "./App.css";
import { BrowserRouter, Route, Link } from "react-router-dom";
import ProductScreen from "./screens/ProductScreen";
import HomeScreen from "./screens/HomeScreen";
import CartScreen from "./screens/CartScreen";
//import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
//import '../assets/'

function App() {
  const openMenu = () => {
    document.querySelector(".sidebar").classList.add("open");
  };
  const closeMenu = () => {
    document.querySelector(".sidebar").classList.remove("open");
  };
  /*Textbox Events*/
$(document).on('focusin', 'navbar input.search-textbox', function(){
    if($(this).val() <= 0){
        var parent = $(this).closest('div.search');
        parent.addClass('focused');
    }
});
$(document).on('focusout', 'navbar input.search-textbox', function(){
    if($(this).val() <= 0){
        var parent = $(this).closest('div.search');
        parent.removeClass('focused');
    }
});
$(document).on('click', 'navbar .search', function(){
    $(this).children('input.search-textbox').focus();
});

/*Text Key Events for Animating Icons i.e. .ico-btn*/
$(document).on('keyup', 'navbar input.search-textbox', function(){
    var parent = $(this).closest('div.search');
    var phrase = $(this).val(),
        phrase_length = phrase.length;

    if(phrase_length >= 2){
        parent.addClass('multi-char');
        if(!parent.hasClass('not-null')){
            parent.addClass('not-null');
        }

    }
    else if(phrase_length == 1){
        parent.addClass('not-null');
        parent.removeClass('multi-char');
    }
    else if(phrase_length <= 0){
        parent.removeClass('not-null');
        parent.removeClass('multi-char');
    }
});

/*Tab Highlighter Functionality*/
$(document).on('click', 'navbar .tabs ul.navbar-body li a', function(){
    var $this = $(this);
    TabHighlighter.set($this);
    $this.closest('li').siblings('.active').removeClass('active');
    $this.closest('li').addClass('active');
});
var TabHighlighter = {
    set: function($this){
        $('.tab-highlighter').css({
            'left':  $this.closest('li').offset().left,
            'width': $this.closest('li').outerWidth()
        });
    },
    refresh: function(){
        var $this = $('.tabs ul.navbar-body li.active a');
        $('.tab-highlighter').css({
            'left':  $this.closest('li').offset().left,
            'width': $this.closest('li').outerWidth()
        });
    }
};
$(window).resize(function(){
    TabHighlighter.refresh();
});
$(document).ready(function(){
	TabHighlighter.refresh();
});
  return (
    <BrowserRouter>
      <div className="grid-container">
        <header className="header">
        <navbar>
  <div className="navbar nav navbar-fixed-top navbar-inverse">
    <div className="container-fluid">
      <div className="v-flex">
        <div className="navbar-body">
          <div className="navbar-start">
            <div className="hamburger-menu">
              <div className="ic_menu">
                            <span></span>
                            <span></span>
                            <span></span>
                        </div>
            </div>
            <img className="logo" src='https://firebasestorage.googleapis.com/v0/b/personal-1c08e.appspot.com/o/logo.svg?alt=media&token=7a322d4e-6088-4647-975a-0f36107a5547'/>
          </div>
          <div className="search">
            <input type="text" className="search-textbox" placeholder="Search"/>
            <a className="ico-btn search-btn"><i className="material-icons ic_search">&#xE8B6;</i></a>
            <a className="ico-btn clear-btn"><i className="material-icons ic_clear">&#xE14C;</i></a>
          </div>
          <div className="navbar-end">
            <a style="text-transform: uppercase">Get in</a>
          </div>
        </div>
      </div>
    </div>
  </div>
  <div className="navbar navbar-fixed-top navbar-inverse tabs paper-shadow-bottom-z-2">
    <div className="container-fluid">
      <ul className="navbar-body list-inline">
        <li className="active"><a className="active">Home</a></li>
        <li><a>Potion Lab</a></li>
        <li><a>Stats</a></li>
        <li><a>System Log Feed</a></li>
      </ul>
      <div className="tab-highlighter"></div>
    </div>
  </div>
</navbar>
        </header>



        <aside className="sidebar">
          <h3>Shopping categories</h3>
          <button className="sidebar-close-button" onClick={closeMenu}>
            x
          </button>
          <ul>
            <li>
              <a href="index.html">Pants</a>
            </li>
            <li>
              <a href="index.html">Shirts</a>
            </li>
          </ul>
        </aside>
        <main className="main">
          <div className="content">
            <Route path="/product/:id" component={ProductScreen} />
            <Route path="/cart/:id?" component={CartScreen} />
            <Route path="/" exact={true} component={HomeScreen} />
          </div>
        </main>
        <footer className="footer">All rights reserved.</footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
